INSERT INTO `setting` (`id`, `pin`, `logo`, `ikut_wakaf`, `judul_web`, `link_download_apk_marketing`, `bg_login`, `bg_pin`, `link_download_apk`, `tentang_kami`, `judul_tentang_kami`, `foto_tentang_kami`, `latitude`, `longitude`, `facebook`, `instagram`, `twitter`, `telegram`, `nama_web`, `alamat`, `visi`, `misi`, `slogan_web`) VALUES
(3,	1234,	'PVC9GRJdOlAVMdZHV7n8vIeNqtyHDpjl.png',	'alur.pdf',	'Wakaf Mudah Pahala Berlimpah',	'marketing',	'pyFYY6vLhuGudN0IPKPqOrcqxmRI6ZNw.jpg',	'1AxYE37bhrsYjudjpPTdf6uY7hqvnHVl.jpg',	'playstore',	'<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Sudah menjadi tanggung jawab kita bersama untuk mengembangkan potensi ditengah ummat Islam dalam negeri ini serta menampilkan langkah-langkah baru yang menjamin kemakmuran dan kesejahteraan masyarakat. Alhamdulillah, Yayasan Inisiator Salam Kariim (disingkat I-SALAM) yang disahkan oleh Kementerian Hukum dan Hak Asasi Manusia dengan surat keputusan nomor AHU-0001429.AH.01.04.Tahun 2021, hadir dengan segenap keyakinan dan optimisme, serta kemampuan sumber daya yang berkualitas Kemandirian ekonomi serta keamanan dan ketahanan ideologi merupakan bagian dari pilar utama tegaknya sebuah negeri.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\"> </span><br /><span style=\"color: #000000;\">Manajemen Yayasan I-SALAM memiliki jaringan yang luas dikenal baik di dalam dan luar negeri dan berpotensi untuk menjalin kerjasama strategis di berbagai bidang. I-Salam adalah amalan jariah yang diharapkan terus bermanfaat dan mengalir pahalanya hingga hari kiamat pada segala kegiatan yang dijalankannya. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Semoga Allah menjadikan I-SALAM ini sebagai wadah yang mengantar kepada peradaban islami yang selalu menebarkan dan membuka pintu kebaikan yang luas bagi para mitra serta membawa keberkahan bagi seluruh masyarakat. </span><br /><span style=\"color: #000000;\">Ayo bekerjasama dengan I-SALAM ! untuk memenuhi kebutuhan pengembangan potensi aset ummat dalam skala nasional sehingga diharapkan memberi hasil yang terbaik bagi masyarakat.</span></p>',	'Lembaga Penyalur Wakaf Isalam',	'aFsuWhLC35Ne5hSuJ4XDmcT3AqhGuMjX.jpg',	'-7.963169587297386',	'112.63095066210936',	'https://www.facebook.com/isalamkarim',	'https://www.instagram.com/isalamkarim/',	'https://twitter.com/isalamkarim',	'https://web.telegram.org/z/',	'ISALAM',	'Batu',	'<p><span style=\"color: #000000;\">Menjadi Lembaga Islam yang unggul, amanah dan profesional dalam mengembangkan potensi keummatan secara nasional yang bermanfaat bagi Umat Islam dalam bidang Dakwah, Sosial, Pendidikan, Ekonomi, dan Pengembangan Aset dengan berlandaskan Al-Qur\'an dan As-Sunnah sesuai pemahaman ParaSalaf</span></p>',	'<p><span style=\"color: #000000;\">1. Mendirikan pusat pendidikan dan pelatihan (pusdiklat) yang unggul dalam mewadahi penyelenggaraan pendidikan dan pelatihan formal maupun informal.</span></p>\r\n<p><span style=\"color: #000000;\">2. Menyelenggarakan program dakwah dan aktifitas sosial yang menyentuh seluruh lapisan masyarakat melalui berbagai media.</span></p>\r\n<p><span style=\"color: #000000;\">3. Memberdayakan potensi ekonomi umat dengan menggalakkan investasi sesuai syariat, pemberdayaan wakaf yang akuntabel serta menanamkan dan menghidupkan kewirausahaan di berbagai sektor untuk kesejahteraan umat Islam.</span></p>\r\n<p><span style=\"color: #000000;\">4. Membangun dan Mengelola Aset untuk kepentingan Umat Islam baik sarana prasarana ibadah, pendidikan, dakwah, ekonomi serta sosial kemasyarakatan</span></p>',	'Ketika manusia meninggal dunia, maka terputus sudah amal jariahnya kecuali tiga perkara yakni: sedekah jariyah, ilmu bermanfaat dan doa anak yang sholeh. ');
